// CLASS: CodeReplacer
class CodeReplacer {

    /**
     * Creates a backup of the file.
     */
    private fun createBackup(filePath: Path): String {
        val timestamp = System.currentTimeMillis()
        val backupPath = Path.of("${filePath}.backup.$timestamp")
        Files.copy(filePath, backupPath, StandardCopyOption.COPY_ATTRIBUTES)
        return backupPath.toString()
    }

    /**
     * Writes content to file atomically by writing to a temporary file first.
     */
    private fun writeFileAtomically(filePath: Path, content: String) {
        val tempPath = Path.of("${filePath}.tmp.${System.currentTimeMillis()}")

        try {
            // Write to temporary file
            tempPath.writeText(content)

            // Atomically move temporary file to target
            Files.move(tempPath, filePath, StandardCopyOption.REPLACE_EXISTING)

        } catch (e: Exception) {
            // Clean up temporary file if something went wrong
            try {
                Files.deleteIfExists(tempPath)
            } catch (cleanupException: Exception) {
                // Ignore cleanup errors
            }
            throw e
        }
    }
}

package com.example.mindweaverstudio.data.utils.codereplacer.models

import java.nio.file.Path
import java.nio.file.Paths

data class ReplacementRequest(
    val filePath: String,
    val originalCode: String,
    val newCode: String,
    val options: ReplacementOptions = ReplacementOptions()
) {
    /**
     * Gets the file path as a Path object.
     */
    fun getPath(): Path = Paths.get(filePath)

    /**
     * Validates the request parameters.
     */
    fun validate(): ReplacementValidation {
        if (filePath.isBlank()) {
            return ReplacementValidation.Invalid("File path cannot be blank")
        }

        if (originalCode.isBlank()) {
            return ReplacementValidation.Invalid("Original code cannot be blank")
        }

        if (originalCode == newCode && !options.allowNoChange) {
            return ReplacementValidation.Invalid("Original and new code are identical")
        }

        return ReplacementValidation.Valid
    }
}

package com.example.mindweaverstudio.data.utils.codereplacer.models

data class ReplacementMatch(
    val startLine: Int,                    // Line number where match starts (1-based)
    val endLine: Int,                      // Line number where match ends (1-based)
    val startColumn: Int,                  // Column where match starts (1-based)
    val endColumn: Int,                    // Column where match ends (1-based)
    val matchedText: String,               // The actual matched text
    val contextBefore: String,             // Lines before the match for context
    val contextAfter: String               // Lines after the match for context
) {
    /**
     * Gets a human-readable location description.
     */
    fun getLocationDescription(): String {
        return if (startLine == endLine) {
            "line $startLine, columns $startColumn-$endColumn"
        } else {
            "lines $startLine-$endLine"
        }
    }
}

