class CodeOrchestrator(
    private val registry: PipelineRegistry,
    private val aiClient: AiClient,
    private val memoryStore: MemoryStore
) {
    private val systemPrompt: ChatMessage = generateSystemPrompt()
    private var messagesHistory: List<ChatMessage> = listOf(systemPrompt)
    private val json: Json = Json {
        ignoreUnknownKeys = true
        explicitNulls = false
        isLenient = true
    }
    private val pipelineRunner = PipelineRunner(memoryStore)

    suspend fun handleMessage(userInput: String): PipelineResult {
        val userMessage = ChatMessage(ROLE_USER, userInput)
        messagesHistory += userMessage

        val aiResult = aiClient.createChatCompletion(
            messages = messagesHistory,
            temperature = 0.3,
            maxTokens = 1024
        )

        return aiResult.fold(
            onSuccess = { result ->
                val decision = json.decodeFromStringOrNull<CodeOrchestratorCommand>(result.message)
                    ?: return@fold errorPipelineResult(message = "ÐÐµ Ð¿Ð¾Ð»ÑƒÑ‡Ð¸Ð»Ð¾ÑÑŒ Ñ€Ð°ÑÐ¿Ð°Ñ€ÑÐ¸Ñ‚ÑŒ Ð¾Ñ‚Ð²ÐµÑ‚ Ð¾Ñ‚ Ð¾Ñ€ÐºÐµÑÑ‚Ñ€Ð°Ñ‚Ð¾Ñ€Ð°")
                val pipeline = registry.get(decision.pipeline)
                    ?: return@fold errorPipelineResult(message = "ÐÐµÐ¸Ð·Ð²ÐµÑÑ‚Ð½Ñ‹Ð¹ Ð°Ð³ÐµÐ½Ñ‚")

                val result = pipelineRunner.execute(
                    pipeline = pipeline,
                    input = userMessage,
                    options = PipelineOptions(resumeFromLast = false),
                )

                return result
            },
            onFailure = {
                return errorPipelineResult(error = it)
            }
        )
    }

    private fun generateSystemPrompt(): ChatMessage {
        val prompt = """
           You are a pipeline orchestrator.
            Your task is to analyze the user message and decide which of the available pipelines should handle this request.
            You never execute any request yourself.
            You always respond strictly in JSON format.

            Here is the list of available pipelines and their purposes:
            ${registry.getPresentableList()}

            JSON response format:
            {"pipeline": "<pipeline_name_from_the_list>"}

            Rules:
              - If the user message matches the functionality of one of the pipelines â†’ specify that pipeline.
              - If it does not match any pipeline â†’ use "chat_pipeline".
              - No comments, no text, no markdown formatting outside of JSON.

        """.trimIndent()

        return ChatMessage(
            role = ROLE_SYSTEM,
            content = prompt,
        )
    }

}

package com.example.mindweaverstudio.data.voiceModels

import com.sun.jna.Native
import org.vosk.LibVosk

class SpeechRecognizer() {
    private val modelPath: String = "/Users/nikitaradionov/IdeaProjects/MindWeaver Studio/composeApp/src/jvmMain/kotlin/com/example/mindweaverstudio/data/voiceModels/vosk-model-small-ru-0.22"

    private var model: Model? = null
    private var recognizer: Recognizer? = null
    private var line: TargetDataLine? = null
    private var job: Job? = null
    val textChannel = Channel<String>(Channel.UNLIMITED)  // ÐšÐ°Ð½Ð°Ð» Ð´Ð»Ñ Ð¿ÐµÑ€ÐµÐ´Ð°Ñ‡Ð¸ Ñ€Ð°ÑÐ¿Ð¾Ð·Ð½Ð°Ð½Ð½Ð¾Ð³Ð¾ Ñ‚ÐµÐºÑÑ‚Ð° Ð°Ð³ÐµÐ½Ñ‚Ñƒ

    init {
        LibVosk.setLogLevel(LogLevel.WARNINGS)
        Native.setProtected(true)  // Ð”Ð»Ñ JNA
        model = Model(modelPath)
        recognizer = Recognizer(model, 16000f)  // 16000 Hz â€” ÑÑ‚Ð°Ð½Ð´Ð°Ñ€Ñ‚ Ð´Ð»Ñ Vosk
    }

    fun startRecognition(scope: CoroutineScope) {
        job = scope.launch(Dispatchers.IO) {
            val format = AudioFormat(16000f, 16, 1, true, false)  // Mono, 16-bit, 16kHz
            val info = DataLine.Info(TargetDataLine::class.java, format)
            line = AudioSystem.getLine(info) as TargetDataLine
            line?.open(format)
            line?.start()

            val buffer = ByteArray(4096)
            while (isActive) {
                val bytesRead = line?.read(buffer, 0, buffer.size) ?: 0
                if (bytesRead > 0 && recognizer?.acceptWaveForm(buffer, bytesRead) == true) {
                    val result = recognizer?.result ?: ""
                    val text = result.substringAfter("\"text\" : \"").substringBeforeLast("\"")  // ÐŸÐ°Ñ€ÑÐ¸Ð½Ð³ JSON-Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚Ð°
                    if (text.isNotBlank()) {
                        textChannel.send(text)  // ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð»ÑÐµÐ¼ Ñ‚ÐµÐºÑÑ‚ Ð°Ð³ÐµÐ½Ñ‚Ñƒ
                    }
                }
            }
        }
    }

    fun stopRecognition() {
        job?.cancel()
        line?.stop()
        line?.close()
        recognizer?.close()
        model?.close()
    }
}
